/*--------------------------------------------------------------
# Pagination 2 Section
--------------------------------------------------------------*/
.pagination-2 {
  padding-top: 0;
}

.pagination-2 nav {
  position: relative;
}

.pagination-2 ul {
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  gap: 8px;
  padding: 0;
  margin: 0;
  list-style: none;
}

.pagination-2 li {
  margin: 0;
  transition: all 0.3s ease-in-out;
}

.pagination-2 li.ellipsis {
  color: color-mix(in srgb, var(--default-color), transparent 40%);
  padding: 8px 16px;
  user-select: none;
}

.pagination-2 li a {
  display: flex;
  align-items: center;
  justify-content: center;
  min-width: 40px;
  height: 40px;
  padding: 8px 16px;
  color: var(--default-color);
  background-color: var(--surface-color);
  border-radius: 8px;
  text-decoration: none;
  transition: all 0.3s ease-in-out;
  box-shadow: 0 2px 4px color-mix(in srgb, var(--default-color), transparent 90%);
}

.pagination-2 li a.active {
  background-color: var(--accent-color);
  color: var(--contrast-color);
  transform: translateY(-2px);
  box-shadow: 0 4px 8px color-mix(in srgb, var(--accent-color), transparent 70%);
}

.pagination-2 li a:hover:not(.active) {
  background-color: color-mix(in srgb, var(--accent-color), transparent 90%);
  transform: translateY(-1px);
  box-shadow: 0 3px 6px color-mix(in srgb, var(--default-color), transparent 85%);
}

.pagination-2 li a i {
  font-size: 14px;
}

.pagination-2 li a span {
  margin: 0 4px;
}

